
public class ArithmeticOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 100 + 50;
	    System.out.println("The Addition is: "+x);
	    
	    int y = 100 - 50;
	    System.out.println("The Substraction is: "+y);
	    
	    int z = 100 * 50;
	    System.out.println("The Multiplication is: "+z);
	    
	    int p = 100 / 50;
	    System.out.println("The Division is: "+p);
	}

}
