
public class LogicalOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 5;
	    System.out.println(x > 3 && x < 10); //And Operator
	    
	    int y = 5;
	    System.out.println(y > 3 || y < 4); //OR Operator
	    
	    int a = 10, b = 1;
	    System.out.println(!(a < b));
        System.out.println( !(a > b));
	}

}
