
public class IncDecOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 50;
		x++;
	    System.out.println("using ++ operator, the value of x is :" +x);
	    
	    int y=50;
	    y--;
	    System.out.println("\nusing -- operator, the value of x is :" +y);
	
	}

}
