
public class MinMax {

	static int n1=10;
	static int n2=20;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out. println("The largest number of the two numbers is " + Math. max(n1,n2));
		System. out. println("The smallest number of the two numbers is " + Math. min(n1,n2));
	}

}
