
public class NumberEqOrNot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int firstNumber = 15;
        int secondNumber = 15;
        if ((firstNumber - secondNumber) == 0)
            System.out.println("Numbers are equal");
        else
            System.out.println("Numbers are not equal");	
		
	}

}
